import {Injectable} from "@angular/core";
import {Task} from "./../models/task.model";
import {find} from "rxjs";
@Injectable({
  providedIn: 'root'
})
export class TaskService {

  private tasks!: Task[];

  constructor() {
    this.tasks = [
      {
        id:0,
        name:"Manger tes morts",
        finished:false
      }
    ]
  }

  getAllTasks(): Task[] {
    return this.tasks;
  }

  getTaskById(id:number): Task{
    const findTask : Task = this.tasks.find((task:Task)=> task.id === id);
    return findTask;
  }

  addTask(newTask: Task):void{
    this.tasks.push(newTask);
  }

}
