import {Injectable} from '@angular/core';
import {HttpClient} from "@angular/common/http";
import {Observable} from "rxjs";
import {User} from "../models/user.model";
import {Task} from "../models/task.model";

@Injectable({
  providedIn: 'root'
})
export class UsersService {

  private users!: User[];

  constructor() {
    this.users = [
      {
        id: 0,
        username:"MagicIrfan",
        email:"bouhenaf.irfan@gmail.com",
        password:"ratio1"
      },
      {
        id: 2,
        username:"MagicIrfan",
        email:"bouhenaf.irfan@gmail.com",
        password:"ratio2"
      },
      {
        id: 1,
        username:"MagicIrfan",
        email:"bouhenaf.irfan@gmail.com",
        password:"ratio3"
      },
    ]
  }

  getAllUsers(): User[] {
    return this.users;
  }

  getUserById(id: number): User{
    return this.users.find((user : User) => user.id === id) as User;
  }

}
