import { Component } from '@angular/core';
import {Task} from "../../../core/models/task.model";
import {FormBuilder, FormControl, FormGroup, Validators} from "@angular/forms";
import {map, Observable} from "rxjs";
import {ActivatedRoute, Router} from "@angular/router";
import {TaskService} from "../../../core/services/task.service";

@Component({
  selector: 'app-full-task',
  templateUrl: './full-task.component.html',
  styleUrls: ['./full-task.component.scss']
})
export class FullTaskComponent {
  task!: Task;
  constructor(private router:Router, private formBuilder: FormBuilder, private taskService:TaskService, private route:ActivatedRoute) {}
  ngOnInit():void {
    const id: number = +this.route.snapshot.params['id'];
    this.task = this.taskService.getTaskById(id);
    console.log(this.task);
  }

  onEditTask(id: number) :void {
    this.router.navigateByUrl(`edit-task/${taskId}`);
  }

  onDeleteTask(id: number) :void {
    this.taskService.deleteTask(id);
    this.router.navigateByUrl('');
  }
}
<div class="my-5 w-50 mx-auto text-light border-2 border-light rounded-3 p-4">
  <h1>Tâche</h1>
  {{task.name}}
  <h1>Description</h1>
  {{task.description}}
  <button class="btn btn-success me-1"><i class="fa-solid fa-eye fa-xl"></i></button>
  <button (click)="onEditTask(task.id)" class="btn btn-primary me-1"><i class="fa-solid fa-pen-to-square fa-xl"></i></button>
  <button (click)="onDeleteTask(task.id)" class="btn btn-danger ms-1"><i class="fa-solid fa-trash fa-xl"></i></button>
</div>
