import {Component, Input} from '@angular/core';
import {Task} from "../../../core/models/task.model";
import {TaskService} from "../../../core/services/task.service";
import {FormBuilder} from "@angular/forms";
import {Router} from "@angular/router";

@Component({
  selector: 'app-task',
  templateUrl: './task.component.html',
  styleUrls: ['./task.component.scss']
})
export class TaskComponent {
  @Input() task!: Task;

  constructor(private taskService: TaskService,private router : Router) {}
  onDeleteTask() : void {
    const index : number = this.taskService.getAllTasks().indexOf(this.task);
    if (index > -1) {
      this.taskService.getAllTasks().splice(index, 1);
    }
  }
  onEditTask(taskId:number) : void {
    this.router.navigateByUrl(`edit-task/${taskId}`);
  }

  onSeeTask(taskId: number) {
    this.router.navigateByUrl(`task/${taskId}`);
  }

  onCheckTask(id: number,checkInput: HTMLElement) {
    console.log("coché");
  }
}
<li class="list-group-item rounded-3 todo-text my-2 mx-2">
  <div class="h-100 div-list-item">
    <p>{{task.name}}</p>
    <div class="float-end">
      <input #checkInput (change)="onCheckTask(task.id,checkInput)" class="form-check-input me-5" type="checkbox" value="" id="myCheck">
      <button (click)="onSeeTask(task.id)" class="btn btn-success me-1"><i class="fa-solid fa-eye fa-xl"></i></button>
      <button (click)="onEditTask(task.id)" class="btn btn-primary me-1"><i class="fa-solid fa-pen-to-square fa-xl"></i></button>
      <button (click)="onDeleteTask()" class="btn btn-danger ms-1"><i class="fa-solid fa-trash fa-xl"></i></button>
    </div>
  </div>
</li>
