import { Component } from '@angular/core';

@Component({
  selector: 'app-task',
  templateUrl: './task.component.html',
  styleUrls: ['./task.component.scss']
})
export class TaskComponent {

}
<li class="list-group-item rounded-3 todo-text my-2 mx-2">
  <div class="h-100 div-list-item">
    <p>Allo</p>
    <div class="float-end">
      <input class="form-check-input me-5" type="checkbox" value="" id="myCheck">
      <button class="btn btn-primary me-1"><i class="fa-solid fa-pen-to-square fa-xl"></i></button>
      <button class="btn btn-danger ms-1"><i class="fa-solid fa-trash fa-xl"></i></button>
    </div>
  </div>
</li>
