import {Injectable} from '@angular/core';
import {User} from "../models/user.model";

@Injectable({
  providedIn: 'root'
})
export class UsersService {

  private users!: User[];

  constructor() {
    this.users = [
      {
        id: 0,
        username:"MagicIrfan",
        email:"bouhenaf.irfan@gmail.com",
        password:"ratio1"
      }
    ]
  }

  getAllUsers(): User[] {
    return this.users;
  }

  getUserById(id: number): User{
    return this.users.find((user : User) : boolean => user.id === id) as User;
  }

  isUserExists(email:string,password:string) :boolean {
    const user : User = this.getAllUsers().find((user : User) => user.email === email && password === password) as User;
    return user !== undefined;
  }

}
