import { Component } from '@angular/core';
import {ActivatedRoute, Router} from "@angular/router";
import {FormBuilder} from "@angular/forms";
import {TaskService} from "../../../core/services/task.service";

@Component({
  selector: 'app-todo-card',
  templateUrl: './todo-card.component.html',
  styleUrls: ['./todo-card.component.scss']
})
export class TodoCardComponent {

  constructor(private router:Router, private formBuilder: FormBuilder, private taskService:TaskService, private route:ActivatedRoute) {}
  onCreateTask() {
    this.router.navigateByUrl('edit-task');
  }
}
<div class="card ms-auto me-auto mt-5 w-50">
  <div class="card-header text-center">
    <div class="clearfix">
      <button class="btn btn-danger" (click)="onCreateTask()"><i class="fa-solid fa-plus fa-2xl"></i> Ajouter une tâche</button>
    </div>
    <ul class="nav nav-tabs text-white card-header-tabs">
      <li class="nav-item border-dark">
        <a class="nav-link text-decoration-none" href="#unfinished" data-bs-toggle="tab">Non Complétés</a>
      </li>
      <li class="nav-item border-dark">
        <a class="nav-link text-decoration-none" href="#finished" data-bs-toggle="tab">Complétés</a>
      </li>
    </ul>
  </div>
  <div class="card-body">
    <div class="tab-content">
      <div class="tab-pane show" id="unfinished">
        <app-task-list [finished]="false"></app-task-list>
      </div>
      <div class="tab-pane show" id="finished">
        <app-task-list [finished]="true"></app-task-list>
      </div>
    </div>
  </div>
</div>
