import { Component, OnInit } from '@angular/core';
import {AuthService} from "../../../core/services/auth.service";
import {UsersService} from "../../../core/services/user.service";
import {Router} from "@angular/router";
import {FormBuilder, FormControl, FormGroup, Validators} from "@angular/forms";

@Component({
  selector: 'app-inscription',
  templateUrl: './inscription.component.html',
  styleUrls: ['./inscription.component.scss']
})
export class InscriptionComponent implements OnInit {

  inscriptionForm!: FormGroup;
  emailControl!:FormControl;
  passwordControl!:FormControl;
  usernameControl!:FormControl;

  constructor(private authService: AuthService, private userService: UsersService, private router : Router, private formBuilder: FormBuilder) { }

  ngOnInit(): void {
    this.emailControl = new FormControl('', [
      Validators.required,
      Validators.email
    ]);

    this.passwordControl = new FormControl('', [
      Validators.required,
      Validators.minLength(4)
    ]);

    this.usernameControl = new FormControl('', [
      Validators.required,
      Validators.minLength(5)
    ]);
  }

  onCreateUser() :void {

  }
}
<div class="container">
  <div class="row justify-content-center mt-5">
    <div class="col-md-6">
      <div class="card dark">
        <div class="card-body">
          <div class="d-flex justify-content-center align-items-center mb-4">
            <img ngSrc="/assets/images/person.png" alt="Avatar" class="rounded-circle me-3" width="200" height="200">
          </div>
          <form [formGroup]="inscriptionForm" (ngSubmit)="onCreateUser()">
            <div class="form-floating mb-3">
              <input type="text" class="form-control" id="username" required>
              <label for="username">Nom d'utilisateur</label>
            </div>
            <div class="form-floating mb-3">
              <input type="email" class="form-control" id="email" required>
              <label for="email">Adresse email</label>
            </div>
            <div class="form-floating mb-3">
              <input type="password" class="form-control" id="password" required>
              <label for="password">Mot de passe</label>
            </div>
            <div class="d-grid gap-2">
              <button type="submit" class="btn btn-dark">S'inscrire</button>
            </div>
          </form>
          <p class="mt-3 mb-0 text-center">Déjà un compte ? <a routerLink="/auth/connexion">Se connecter maintenant</a></p>
        </div>
      </div>
    </div>
  </div>
</div>
