import { Component, OnInit } from '@angular/core';
import {FormBuilder, FormControl, FormGroup, Validators} from "@angular/forms";
import {Router} from "@angular/router";
import {UsersService} from "../../../core/services/user.service";
import {AuthService} from "../../../core/services/auth.service";

@Component({
  selector: 'app-connection',
  templateUrl: './connection.component.html',
  styleUrls: ['./connection.component.scss']
})
export class ConnectionComponent implements OnInit {

  connectionForm!: FormGroup;
  emailControl!:FormControl;
  passwordControl!:FormControl;
  constructor(private authService: AuthService, private userService: UsersService, private router : Router, private formBuilder: FormBuilder) { }

  ngOnInit(): void {
    this.emailControl = new FormControl('', [
      Validators.required,
      Validators.email
    ]);

    this.passwordControl = new FormControl('', [
      Validators.required,
      Validators.minLength(4)
    ]);
    this.connectionForm = this.formBuilder.group({
      email: this.emailControl,
      password: this.passwordControl
    }, {
      updateOn: 'change'
    });
  }

  onLogin() : void{
      const formValue = this.connectionForm!.value;
      console.log(formValue);
      console.log(this.userService.getAllUsers());
      if(this.userService.isUserExists(formValue['email'],formValue['password'])){
        this.authService.login();
        this.router.navigateByUrl("/");
      }
      else{
        this.router.navigateByUrl("/auth/connexion");
      }

  }

}
<div class="container">
  <div class="row justify-content-center mt-5">
    <div class="col-md-6">
      <div class="card dark">
        <div class="card-body">
          <div class="d-flex justify-content-center align-items-center mb-4">
            <img ngSrc="/assets/images/person.png" alt="Avatar" class="rounded-circle me-3" width="200" height="200">
          </div>
          <form [formGroup]="connectionForm" (ngSubmit)="onLogin()">
            <div class="form-floating mb-3">
              <input type="email" class="form-control" id="email" formControlName="email" required>
              <label for="email">Adresse email</label>
              <p class="text-danger text-small mt-2" *ngIf="emailControl.errors?.['email']">L'email est invalide</p>
            </div>
            <div class="form-floating mb-3">
              <input type="password" class="form-control" id="password" formControlName="password" required>
              <label for="password">Mot de passe</label>
            </div>
            <div class="d-grid gap-2">
              <button type="submit" class="btn btn-dark" [disabled]="connectionForm.invalid">Se connecter</button>
            </div>
          </form>
          <p class="mt-3 text-center">Pas de compte ? <a routerLink="/auth/inscription">S'inscrire maintenant</a></p>
        </div>
      </div>
    </div>
  </div>
</div>
