import { Component, OnInit  } from '@angular/core';
import {TaskService} from "../../../core/services/task.service";
import {FormBuilder, FormGroup, Validators} from "@angular/forms";
import {Task} from "./../../../core/models/task.model";

@Component({
  selector: 'app-create-task',
  templateUrl: './create-task.component.html',
  styleUrls: ['./create-task.component.scss']
})
export class CreateTaskComponent {
  newTaskForm!: FormGroup;
  constructor(private taskService: TaskService,private formBuilder: FormBuilder) {}
  ngOnInit() : void{
    this.newTaskForm = this.formBuilder.group({
      newTask: [null, [Validators.required]]
    });
  }
  onCreateTask() : void {
    const formValue = this.newTaskForm!.value;
     const newTask : Task = new Task(
       1,
       formValue['newTask'],
       false,
     );
     this.taskService.addTask(newTask);
  }
}
<form class="row g-2" [formGroup]="newTaskForm">
  <div class="form-floating col-10">
    <input type="text" class="form-control text-light" id="new-task" formControlName="newTask" placeholder="Saisir une nouvelle tâche" required/>
    <label for="new-task" class="text-light">Saisir une nouvelle tâche</label>
  </div>
  <div class="col-2">
    <button (click)="onCreateTask()" class="btn btn-danger h-100 w-75" [disabled]="newTaskForm.invalid"><i class="fa-solid fa-plus fa-2xl"></i></button>
  </div>
</form>
