import { Component } from '@angular/core';

@Component({
  selector: 'app-create-task',
  templateUrl: './create-task.component.html',
  styleUrls: ['./create-task.component.scss']
})
export class CreateTaskComponent {

}
<form class="row g-2">
  <div class="form-floating col-10">
    <input type="text" class="form-control text-light" id="new-task" placeholder="Saisir une nouvelle tâche"/>
    <label for="new-task" class="text-light">Saisir une nouvelle tâche</label>
  </div>
  <div class="col-2">
    <button class="btn btn-danger h-100 w-75"><i class="fa-solid fa-plus fa-2xl"></i></button>
  </div>
</form>
