import { Component } from '@angular/core';
import {Router} from "@angular/router";
import {map, Observable} from "rxjs";
import {Task} from "../../../core/models/task.model";
import { OnInit } from '@angular/core';
import {FormBuilder, FormGroup, Validators} from "@angular/forms";

@Component({
  selector: 'app-edit-task',
  templateUrl: './edit-task.component.html',
  styleUrls: ['./edit-task.component.scss']
})
export class EditTaskComponent {

  editTaskForm!: FormGroup;
  previewTask$!: Observable<Task>;
  constructor(private router:Router, private formBuilder: FormBuilder) {}

  ngOnInit():void{
    this.editTaskForm = this.formBuilder.group({
      taskName: [null, [Validators.required]],
      taskDescription: [null, [Validators.required]]
    });
    this.previewTask$ = this.editTaskForm.valueChanges.pipe(
      map((task: Task) => ({
        ...task,
        id:0,
        finished:false
      })));
  }

  onEditTask():void{
    this.router.navigateByUrl("");
  }

  onReturn() : void {
    this.router.navigateByUrl("");
  }
}
<div class="edit-task border-2 w-50 mx-auto text-light border-2 rounded-3 p-4">
  <form [formGroup]="editTaskForm">
    <div class="form-floating mb-3">
      <input type="text" class="form-control" id="task-name" formControlName="taskName">
      <label for="task-name">Tâche</label>
    </div>
    <div class="form-floating mb-3">
      <textarea class="form-control" id="task-description" formControlName="taskDescription"></textarea>
      <label for="task-description">Description</label>
    </div>
    <div class="clearfix">
      <div class="float-start">
        <button type="button" (click)="onReturn()" class="btn dark rounded-4">Retour</button>
      </div>
      <div class="float-end">
        <button type="submit" (click)="onEditTask()" class="btn dark rounded-4">Sauvegarder</button>
      </div>
    </div>
  </form>
</div>
